import EditBooking from "./EditBooking";
import React from "react"

const Booking = ({booking, editBookingForm, updatedBookings}) => {
      return (
      <div className="box-wrap">
        {/* {console.log(booking.drone.model)} */}
         {/* {drones.map(bookedDrone => {bookedDrone.model})} */}
            {/* <h2>{booking.drone.image}</h2> */}

            {/* <img style={{ width: "450px", height: "350px", objectFit: "cover" }} src={booking.drone.image} /> */}
            <h2>{booking.drone.model}</h2>
            <h2>From: {booking.from_address}</h2>
            <h2>To: {booking.to_address}</h2>

            <button className='move_button' onClick={this.displayForm} >Edit form</button> 
          <button className='move_button' onClick={()=> editBookingForm(booking)} >Edit Delivery</button>
          <EditBooking updatedBookings={updatedBookings}/>
          {this.state.showForm ? <BookingForm drones={this.props.drones} addNewBooking={this.props.addNewBooking}/> : null}
      </div>
    );


}

export default Booking;
