import BookingForm from "./BookingForm";
import React, { Component } from 'react';

class Drone extends Component {

  state = {
    showForm: false
  }
  showForm = () =>{
    this.setState({showForm: !this.state.showForm})
  }

    render() {
      return (
      <div className="box-wrap">
          <img style={{ width: "450px", height: "350px", objectFit: "cover" }} src={this.props.drone.image} />
          <div className="drone-name">
            <h2>{this.props.drone.model}</h2>
            <h2>{this.props.drone.category}</h2>
          </div>
          <button onClick={this.showForm} type="submit">Book Delivery</button>
          {this.showForm ? <BookingForm /> : null}
      </div>
    );
  }

}

export default Drone;
