import React, { Fragment } from "react";
import { Route, Switch, Redirect, withRouter } from 'react-router-dom'
import './App.css';
import './index.js'
import LoginForm from './components/LoginForm'
// import NotFound from './'
import MainContainer from './components/MainContainer'


class App extends React.Component {

  state = {
    currentUser: null
  }

  updateCurrentUser = (user) =>{
    this.setState({
      currentUser: user 
    })
  }


  render(){
    return (
      <Fragment>
        <Nav user={this.state.currentUser} updateCurrentUser={this.updateCurrentUser}/>
        <Switch>
          <Route exact path="/" render={() => <Redirect to="/login" />} />
          <Route exact path="/MainContainer" render={() => (
            this.state.currentUser ?  <MainContainer currentUser={this.state.currentUser}/> : <Redirect to="/login"/>
          ) } />
          <Route exact path="/login" render={() => (
            this.state.currentUser == null ? <LoginForm updateCurrentUser={this.updateCurrentUser} /> : <Redirect to="/profile"/>
          )} />
          {/* <Route component={NotFound} /> */}
        </Switch>
      </Fragment>
    )
  }
}
