import React from "react";
import './App.css';
import './index.js'
import MyInfo from './components/MyInfo'
import MyForm from "./components/MyForm.js"
let URL = "http://localhost:3000/drones"

class App extends React.Component {
  constructor(){
    super()
    console.log('App.js in Constructor')
    this.state = {
      drones: [],
    }
  }

  componentDidMount(){
    //function from react library
    console.log('App.js in ComponentDidMount')

    fetch(URL)
    .then(res => res.json())
    .then(drones => {
      this.setState({
        drones: drones 

      })
      // console.log(drones)
    })

  }

  toggleDragon = (updatedDragon) => {
    //toggleDragon
    console.log(updatedDragon, "This was clicked! ---- Raul ");
    //chamge atWar attribute 


    this.setState({
      dragons: [...this.state.dragons.map((dragon)=> dragon.id == updatedDragon.id ?updatedDragon : dragon)]
    })
  };


  render(){
  return (
    <div className="App">
     <h1>Welcome to My App!</h1>
     <MyInfo />
     <MyForm />
    </div>
    ) 
  }
}

export default App;
