import React from "react";
import './App.css';
import './index.js'
import MyInfo from './components/MyInfo'
import MyForm from "./components/MyForm.js"
let URL = "http://localhost:3000/drones"

class App extends React.Component {
  constructor(){
    super()
    console.log('App.js in Constructor')
    this.state = {
      drones: [],
    }
  }

  componentDidMount(){
    //function from react library
    console.log('App.js in ComponentDidMount')

    fetch(URL)
    .then(res => res.json())
    .then(drones => {
      this.setState({
        drones: drones 

      })
      // console.log(drones)
    })

  }

  droneHandle = (updatedDrone) => {

    this.setState({
      drones: [...this.state.drones.map((drone)=> drone.id == updatedDrone.id ? updatedDrone : drone)]
    })
  };


  render(){
  return (
    <div className="App" >
     <h1>Welcome to DroneS!</h1>
     <MyInfo title="Drones Available" droneHandle={this.droneHandle} drones={this.state.drones}/>

     <MyForm />
    </div>
    ) 
  }
}

export default App;
