import React, { Component } from "react";
import DroneList from "./DroneList";
import SearchBar from "./SearchBar";

class MainContainer extends Component {

    state = {
        drones: []
      }


    componentDidMount(){
      fetch('http://localhost:3000/drones')
      .then(res => res.json())
      .then(drones => {
        this.setState({
          drones: drones  
        })
        // console.log(drones)
      })
    }

    showForm = () =>{
      this.setState({showForm: !this.state.showForm})
    }

    deleteBooking = (artist) =>{
      // console.log(artist)
      fetch('http://localhost:3000/artists/'+artist.id, {method: 'DELETE'})
      .then(r => r.json())
      .then(()=>{
        this.setState({
          artists: this.state.artists.filter(deletedArtist => deletedArtist.id !== artist.id)
        })
      })
    }


    render(){
    return (
      <div>
       <SearchBar/>
       <DroneList drones={this.state.drones}/>

      </div>
      ) 
    }
  }

  export default MainContainer
