import React from 'react'
import { Button, Form, Grid, Header, Image, Message, Segment } from 'semantic-ui-react'


class LoginForm extends React.Component {
  state = {
    email: "",
    password: ""
  };

  handleChange = (e, { name, value }) => {
    this.setState({ [name]: value });
  };

  //When form is submitted, make a fetch to "log in the user"
  handleLoginSubmit = () => {
    console.log("attempting to log in")
    fetch("http://localhost:3000/login", {
      method:"POST",
      headers: {
        "Content-Type" : "application/json",
        "Accept" : "application/json"
      },
      body: JSON.stringify({
        email: this.state.email,
        password: this.state.password
      })
    }).then(res => res.json())
    .then(data => {
       if(data.error_message){
        alert(data.error_message)
            }else{
                //succesful log in
            localStorage.setItem("token", data.token)
            this.props.updateCurrentUser(data.user_data)
        }

    })
  }
  render() {
        return (
  <Grid textAlign='center' style={{ height: '2vh' }} verticalAlign='middle'>
    <Grid.Column style={{ maxWidth: 450 }}>
      <Header as='h2' color='teal' textAlign='center'>
        <Image style={{ width: "120px", height: "120px", objectFit: "cover" }} src="https://thumbs.dreamstime.com/t/vector-drone-prohibited-sign-illustration-colorful-flat-design-no-zone-quadcopter-red-background-70448549.jpg" />
      </Header>
      <Header as='h2' color='teal' textAlign='center'>
      Log-in to your account
      </Header>
      <Form size='large'
          onSubmit={this.handleLoginSubmit}
          size="mini"
          key="mini"
          loading={this.props.authenticatingUser}
          error={this.props.failedLogin}
        >
           <Message
            error
            header={this.props.failedLogin ? this.props.error : null}
          />
        <Segment stacked>
          <Form.Input 
          fluid icon='user' iconPosition='left' placeholder='E-mail address'
              // label="email"
              name="email"
              onChange={this.handleChange}
              value={this.state.email}
           />
          {/* <Form.Input
              fluid
              icon='lock'
              iconPosition='left'
              placeholder='Password'
              type='password'
              // name="password"
              onChange={this.handleChange}
              value={this.state.email}
            /> */}
              <Form.Input
              fluid
              icon='lock'
              iconPosition='left'
              type="password"
              // label="password"
              placeholder="password"
              name="password"
              onChange={this.handleChange}
              value={this.state.password}
            />
         {/* <Button type="submit">Login</Button> */}

          <Button color='teal' fluid size='small' type="submit">
            Login
          </Button>
        </Segment>
      </Form>
    </Grid.Column>
  </Grid>
          )
        }
      } 

export default LoginForm

