import React from 'react'

class BookingForm extends React.Component{
    //state goes here
    state = {
        from_address: '',
        to_address: '',
        drone_id: '',
        user_id: ''
      }

      handleSubmit = (e) => {
        e.preventDefault()
        // console.log(this.state)
        let newBooking = {
          from_address: this.state.from_address,
          to_address: this.state.to_address,
          drone_id: this.state.drone_id,
          user_id: this.state.user_id
        }
        // console.log(newBooking)
        let reqPack = {
          headers: {"Content-Type":"application/json"},
          method: 'POST',
          body: JSON.stringify(newBooking)
        }
        // console.log(reqPack)
        fetch('http://localhost:3000/bookings', reqPack)
        .then(r => r.json())
        // .then(bookingData => this.props.addNewBooking(bookingData))
        .then(console.log)
      }



      render() {
        return (
          <div className="container">

            <form onSubmit={this.handleSubmit} className="add-form">

              <input onChange={(e)=>this.setState({from_address: e.target.value})} type="text" name="name" placeholder="Enter From Address" className="input-text"/>
              <br/>
              <input onChange={(e)=>this.setState({to_address: e.target.value})} type="text" name="image" placeholder="Enter To Address" className="input-text"/>
              <br/>
              <input name="hiddenInput" value="hiddenValue" type="hidden" />
              <div >
                <select onChange={(e)=>this.setState({drone_id: e.target.value})} className="form-control">
                {this.props.drones.map(drone => <option value="drone">{drone.model} {drone.id}</option>)
                }
                </select>
              </div>
              <div >
                <select onChange={(e)=>this.setState({drone_id: e.target.value})} className="form-control">
                {this.props.drones.map(drone => <option value="drone">{drone.model} {drone.id}</option>)
                }
                </select>
              </div>
              <br/>
              <input type="submit" name="submit" value="Submit" className="submit"/>
            </form>
          </div>
        );
      }

    }
export default BookingForm;
