import React from "react";
import './App.css';
import './index.js'
import MyInfo from './components/MyInfo'
import MyForm from "./components/MyForm.js"
let URL = "http://localhost:3000/drones"

class App extends React.Component {
  constructor(){
    super()
    console.log('App.js in Constructor')
    this.state = {
      drones: [],
    }
  }

  componentDidMount(){
    //function from react library
    console.log('App.js in ComponentDidMount')

    fetch(URL)
    .then(res => res.json())
    .then(drones => {
      this.setState({
        drones: drones 

      })
      // console.log(drones)
    })

  }



  droneHandle = (updatedDrone) => {

    this.setState({
      drones: [...this.state.drones.map((drone)=> drone.id == updatedDrone.id ? updatedDrone : drone)]
    })
  }

  handleSubmit = (e) => {
    //preventDefault because form
    e.preventDefault();
    console.log(e.target.rentprice.value)
    //format our data 
    let newDrone = {
      name: e.target.name.value,
      description: e.target.description.value,
      image: e.target.image.value,
      rentprice: e.target.rentprice.value
    };

    let reqPackage = {
      method: 'POST',
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(newDrone)
    }
    //request to add new dragon
    fetch(URL, reqPackage)
    //parsing request
    .then(res => res.json())
    //received new dragon
    .then(newDrone => {

      console.log(newDrone)
      this.setState({
        drones: [...this.state.drones, newDrone]
      })

    })
  }

  render(){
  return (
    <div className="App" >
     <h1>Welcome to DroneShareShip!</h1>
     <MyInfo title="Drones Available" droneHandle={this.droneHandle} drones={this.state.drones}/>

     <MyForm handleSubmit={this.handleSubmit}/>
    </div>
    ) 
  }
}

export default App
