import React, { Component } from "react";
import DroneList from "./DroneList";
import SearchBar from "./SearchBar";
import BookingForm from './BookingForm'
class MainContainer extends Component {

    state = {
        drones: [],
        searchText: '',
        showForm: false
      }


    componentDidMount(){
      fetch('http://localhost:3000/drones')
      .then(res => res.json())
      .then(drones => {
        this.setState({
          drones: drones  
        })
        // console.log(drones)
      })
    }

    showForm = () =>{
      this.setState({showForm: !this.state.showForm})
    }

    searchBar = (textT)=>{
      // console.log(textT)
      this.setState({
        searchText: textT
      })
    }

    render(){
      // const filteredConcerts = this.state.artists.filter(filterConcert => filterConcert.name.toLowerCase().includes(this.state.searchText))
    return (
      <div>
       <SearchBar searchBar={this.searchBar} searchText={this.state.searchText}/>
       <DroneList drones={this.state.drones}
       showForm={this.showForm}/>
      </div>
      ) 
    }
  }

  export default MainContainer
