import EditBooking from "./EditBooking";
import React from "react"

const Booking = ({booking, clickAction, updatedBookings, showForm }) => {
      return (
      <div className="box-wrap">
        {/* {console.log(booking.drone.model)} */} 
            {/* <img style={{ width: "450px", height: "350px", objectFit: "cover" }} src={booking.drone.image} /> */}
            <h2>{booking.drone.model}</h2>
            <h2>From: {booking.from_address}</h2>
            <h2>To: {booking.to_address}</h2>

            {/* <button className='move_button' onClick={displayForm} >Edit form</button>  */}
          <button className='move_button' onClick={()=> clickAction(booking) } >Edit Delivery</button>

          {showForm ?  <EditBooking updatedBookings={updatedBookings}/> : null}

      </div>
    );


}

export default Booking;
