import React, { Fragment } from "react";
import { Route, Switch, Redirect, withRouter } from 'react-router-dom'
import './App.css';
import './index.js'
import LoginForm from './components/LoginForm'
// import NotFound from './'
import MainContainer from './components/MainContainer'


class App extends React.Component {

  state = {
    currentUser: null
  }

  updateCurrentUser = (user) =>{
    this.setState({
      currentUser: user 
    })
  }



  render(){
  return (
    <Fragment>
    {/* <MainContainer/> */}
    <Switch>
      <Route exact path='/' render={()=> <Redirect to='/LoginForm' />}/>
      <Route exact path= '/MainContainer' render={()=>(
      this.state.currentUser ? <MainContainer currentUser={this.state.currentUser}/> : <Redirect to='/LoginForm'/>)} />
      <Route exact path='/' render={()=> (
      this.state.currentUser == null ? <LoginForm updateCurrentUser={this.updateCurrentUser}/> : <Redirect to='/MainContainer/'/>)} />
      {/* <Route component={NotFound} /> */}
    </Switch>
    </Fragment>
    ) 
  }
}

export default App
